mapper.select:
SELECT row_number() OVER () as rnum, defect.* , ntm_schemas.GET_CODENAME('B001', defect.defect_code) defect_name, ntm_schemas.GET_CODENAME('A001', defect.test_type_id) test_type_name, to_char(defect.reg_date, 'YYYY-MM-DD') reg_date_str FROM ntm_schemas.itm_defect defect
WHERE 1=1
AND defect.scenario_code = #{scenario_code}
AND defect.case_code = #{case_code}
